mapper.insert:
INSERT INTO	REPLY	(
						NO
						, MEMBER_NO
						, COMMUNITY_NO
						, BODY
						, REGIST_DATE
						, PARENT_REPLY_NO
						, LOVE_COUNT
						, HATE_COUNT
					)
VALUES				(
						#{no}
						, #{memberNo}
						, #{communityNo}
						, #{body}
						, SYSDATE
						, #{parentReplyNo}
						, 0
						, 0
					)

mapper.update:
UPDATE	REPLY
SET		LOVE_COUNT = LOVE_COUNT + 1
WHERE	NO = #{no}

UPDATE	REPLY
SET		HATE_COUNT = HATE_COUNT + 1
WHERE	NO = #{no}

UPDATE	REPLY
SET		BODY = 'removedReply'
WHERE	NO = #{no}
