mapper.insert:
INSERT INTO COMMUNITY (
							NO
							, MEMBER_NO
							, TITLE
							, BODY
							, WRITE_DATE
							, VIEW_COUNT
							, RECOMMEND_COUNT
							, REQUEST_IP
							, DISPLAY_FILE_NAME
					  )
VALUES				  (
							COMMUNITY_NO_SEQ.NEXTVAL
							, #{memberNo}
							, #{title}
							, #{body}
							, SYSDATE
							, 0
							, 0
							, #{requestIp}
							, #{displayFilename}
					  )

mapper.update:
UPDATE	COMMUNITY
SET		VIEW_COUNT = VIEW_COUNT + 1
WHERE	NO = #{no}

UPDATE	COMMUNITY
SET		RECOMMEND_COUNT = RECOMMEND_COUNT + 1
WHERE	NO = #{no}

UPDATE	COMMUNITY
SET		WRITE_DATE = SYSDATE

		, REQUEST_IP = #{requestIp}


		, TITLE = #{title}


		, BODY = #{body}


		, DISPLAY_FILE_NAME = #{displayFilename}

WHERE	NO = #{no}
AND		MEMBER_NO = #{memberNo}
