mapper.insert:
INSERT INTO MEMBER	(
						NO
						, ID
						, PASSWORD
						, NICKNAME
						, REGIST_DATE
						, SELF_INTRODUCTION
						, PROFILE_FILE_NAME
					)
VALUES 				(
						MEMBER_NO_SEQ.NEXTVAL
						, #{id}
						, #{password}
						, #{nickname}
						, SYSDATE
						, #{selfIntroduction}
						, #{profileFilename}
					)

mapper.update:
UPDATE	MEMBER
SET		MODIFY_DATE = SYSDATE

		, NICKNAME = #{nickname}


		, SELF_INTRODUCTION = #{selfIntroduction}


		, PROFILE_FILE_NAME = #{profileFilename}

WHERE	NO = #{no}

mapper.select:
SELECT	COUNT(ID)
FROM	MEMBER
WHERE	ID = #{id}
